import React, { useContext, useState, useEffect } from 'react';
import '../My-Trips/my-trips.css';
import axios from 'axios';
import { UserContext } from '../../contexts/UserContext';
import TripCard from '../TripCard/TripCard';
import { Row } from 'react-bootstrap';
import { baseURL } from '../../App';
// import { v4 as uuidv4 } from 'uuid';

export default function MyTrips() {
	const { currentUser } = useContext(UserContext);
	const [allTrips, setAllTrips] = useState([]);
	const [myTrips, setMyTrips] = useState([]);

	useEffect(() => {
		const showMyTrips = async () => {
			const res = await axios.get(`${baseURL}/api/alltrips`);
			setAllTrips(res.data);
			setMyTrips(allTrips.filter(trip => trip.email === currentUser.email));
		};
		showMyTrips();
	}, []);

	// const filterTrips = () => {
	// 	const filtered = allTrips.filter(trip => trip.email === currentUser.email);
	// 	console.log(filtered);
	// 	return filtered;
	// };

	return (
		<div>
			<h1>My Trips</h1>
			{myTrips ? (
				<Row className='m-3'>
					{myTrips.map(trip => (
						<TripCard key={trip._id} trip={trip} />
					))}
				</Row>
			) : (
				<p>You didn't register any trips</p>
			)}
		</div>
	);
}
