import { useEffect, useState } from 'react';
import axios from 'axios';
import { Row, Form, Button } from 'react-bootstrap';
import TripCard from './TripCard';
import React from 'react';
import { areas } from './add-trip';
import { baseURL } from '../App';
import './allTrips.css';

export default function AllTrips() {
	const [allTrips, setAllTrips] = useState([]);
	const [areaTrips, setAreaTrips] = useState([]);
	const [searchArea, setSearchArea] = useState();

	useEffect(() => {
		const showAllTrips = async () => {
			const res = await axios.get(`${baseURL}/api/alltrips`);
			setAllTrips(res.data);
		};
		showAllTrips();
	}, [allTrips]);

	const handleSearch = async event => {
		event.preventDefault();
		const newSearch = {
			searchArea: searchArea,
		};
		const res = await axios.get(
			`${baseURL}api/trips/search?area=${searchArea}`
		);
		setAreaTrips(res.data);
		console.log(newSearch);
	};

	return (
		<>
			<div className='container mb-5 '>
				<h1>Available Trips</h1>
				<Form>
					<Form.Group controlId='Type'>
						<Form.Label inline className='mt-1'>
							Select your area
						</Form.Label>
						<Form.Control
							style={{ width: '200px' }}
							as='select'
							placeholder='choose your area'
							value={searchArea}
							required
							onChange={e => setSearchArea(e.target.value)}>
							{areas.map(area => (
								<option key={area.id} area={{ area }}>
									{area}
								</option>
							))}
						</Form.Control>
						<Button
							onClick={handleSearch}
							className='button w-100'
							type='submit'></Button>
					</Form.Group>
				</Form>
				{allTrips && (
					<Row className='m-3'>
						{allTrips.map(trip => (
							<TripCard key={trip.id} trip={trip} />
						))}
					</Row>
				)}
			</div>
		</>
	);
}
