import React, { useEffect, useState, createRef } from 'react';
import './App.css';
import { Nav, Navbar, Container } from 'react-bootstrap';
import Home from './components/Home';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import ModalLogIn from './components/ModalLogIn';
import ModalSignUp from './components/ModalSignUp';
import AddTrip from './components/Add-Trip/add-trip';
import AllTrips from './components/AllTrips/AllTrips';
import { UserContext } from './contexts/UserContext';
import Footer from './components/Footer';
import MyTrips from './components/My-Trips/MyTrips';

export const baseURL = 'http://localhost:5000';

function App() {
	const [currentUser, setCurrentUser] = useState('');
	const wrapper = createRef();

	useEffect(() => {
		const loggedInUser = localStorage.getItem('user');
		if (loggedInUser) {
			const foundUser = JSON.parse(loggedInUser);
			setCurrentUser(foundUser);
		}
		const node = wrapper.current;
	}, []);

	const value = {
		currentUser,
		setCurrentUser,
		baseURL,
	};

	return (
		<UserContext.Provider value={value}>
			<Router>
				<div className=' container-fluid w-100 '>
					<div className='row p-5'>
						<Navbar
							bg='dark'
							variant='dark'
							className='navbar-expand-lg navbar navbar-dark bg-primary shadow-lg'
							fixed='top'>
							<Nav className='collapse navbar-collapse d-flex justify-content-between'>
								<Link className='nav' to='/'>
									Home
								</Link>
								{currentUser && (
									<Link className='nav' to='/mytrips'>
										My Trips
									</Link>
								)}
								<Link className='nav' to='/alltrips'>
									All Trips
								</Link>
								<form className='form-inline my-2 my-lg-0 float-end'>
									<ModalLogIn />
									<ModalSignUp />
								</form>
							</Nav>
						</Navbar>
					</div>
					<Container m-4>
						<Switch>
							<Route exact path='/'>
								<Home />
							</Route>
							<Route path='/add-trip'>
								<AddTrip />
							</Route>
							<Route path='/alltrips'>
								<AllTrips />
							</Route>
							<Route path='/mytrips'>
								<MyTrips />
							</Route>
						</Switch>
					</Container>

					<Footer />
				</div>
			</Router>
		</UserContext.Provider>
	);
}

export default App;
