import { useEffect, useState } from 'react';
import axios from 'axios';
import { Row, Form, Button, Col } from 'react-bootstrap';
import TripCard from './TripCard';
import React from 'react';
import { areas } from './add-trip';
import { baseURL } from '../App';
import './allTrips.css';

export default function AllTrips() {
	const [allTrips, setAllTrips] = useState([]);
	const [areaTrips, setAreaTrips] = useState([]);
	const [searchArea, setSearchArea] = useState();

	// const handleSearch = async event => {
	// 	event.preventDefault();
	// 	setSearchArea(searchArea);
	// 	const res = await axios.get(
	// 		`http://localhost:5000/api/alltrips/search?area=${searchArea}`
	// 	);
	// 	setAreaTrips(res.data);
	// 	console.log(areaTrips);
	// };

	useEffect(() => {
		const showAllTrips = async () => {
			const res = await axios.get(`${baseURL}/api/alltrips`);
			setAllTrips(res.data);
		};
		showAllTrips();
	}, []);

	const handleSearch = e => {
		e.preventDefault();
		const showAllTrips = async () => {
			const res = await axios.get(`${baseURL}/api/alltrips`);
			setSearchArea(res.data.area);
			const result = allTrips.filter(searchArea);
			// setAreaTrips(searchArea.filter(area));
			console.log(result);
		};
		console.log(searchArea);
		// return searchResult;
	};

	return (
		<>
			<div className='container mb-5 '>
				<h1>Available Trips</h1>

				<Form>
					<Form.Group controlId='searchArea'>
						<Row className='justify-content-md-center'>
							<Col xs lg='2'></Col>
							<Col md='auto'>
								<Form.Control
									aria-label='Select your area'
									style={{ width: '200px' }}
									as='select'
									defaultValue='Choose your area'
									value={searchArea}
									required
									onChange={e => setSearchArea(e.target.value)}>
									{areas.map(area => (
										<option key={area.id} area={{ area }}>
											{area}
										</option>
									))}
								</Form.Control>
							</Col>
							<Col md='auto'>
								<Button
									className='searchBtn'
									onClick={handleSearch}
									type='submit'>
									Search
								</Button>
							</Col>
							<Col xs lg='2'></Col>
						</Row>
					</Form.Group>
				</Form>
				{allTrips && (
					<Row className='m-3'>
						{allTrips.map(trip => (
							<TripCard key={trip.id} trip={trip} />
						))}
					</Row>
				)}
				{/* <Row className='m-3'>
				{searchResult && (
						{searchResult.map(trip => (
							<TripCard key={trip.id} trip={trip} />
						))}
					</Row>
				)} */}
			</div>
		</>
	);
}
