import React, { useContext, useState, useEffect } from 'react';
import '../My-Trips/my-trips.css';
import axios from 'axios';
import { UserContext } from '../../contexts/UserContext';
import TripCard from '../TripCard/TripCard';
import { Row } from 'react-bootstrap';
import { baseURL } from '../../App';

export default function MyTrips() {
	const { currentUser } = useContext(UserContext);
	const [allTrips, setAllTrips] = useState();
	const [myTrips, setMyTrips] = useState();

	useEffect(() => {
		// if (currentUser === '')
		// 	setMessage(`You have to be logged in to see this page!`);
		async function filter(trips) {
			const result = trips.filter(trips => currentUser.email === trip.email);
			console.log(result);
			return result;
		}
		const showTrips = async () => {
			const res = await axios.get(`${baseURL}/api/alltrips`);
			setAllTrips(res.data);

			setMyTrips(allTrips.filter(trip => currentUser.email === trip.email));
		};
		showTrips();
	}, []);

	return (
		<div className='mytrips'>
			<h1>My Trips</h1>

			{currentUser && (
				<Row className='m-3'>
					{myTrips.map(trip => (
						<TripCard key={trip._id} trip={trip} />
					))}
				</Row>
			)}
		</div>
	);
}
